import 'dart:convert';

class AccountRes{
  int id;
  String name;
  String pass;
  AccData data;
  PRIVILEGE privilege;
  String role;

  AccountRes({
    required this.id,
    required this.name,
    required this.pass,
    required this.data,
    required this.privilege,
    required this.role
  });

  factory AccountRes.fromJson(ACC_TYPE accType, dynamic json){
    return AccountRes(
      id: json["id"],
      name: json["name"],
      pass: json["pass"] ?? "",
      data: AccData.fromJson(accType, jsonDecode(json["data"])),
      privilege: PRIVILEGE_EXT.parseByName(json["privilege"]),
      role: json["role"] ?? "",
    );
  }

  Map<String, dynamic> toJson(){
    Map<String, dynamic> map = {};
    map['id'] = this.id;
    map['name'] = this.name;
    map['pass'] = this.pass;
    map['data'] = this.data;
    map['privilege'] = this.privilege.getName();
    map['role'] = this.role;
    return map;
  }

  @override
  String toString() {
    return jsonEncode(toJson());
  }

}

class AccData{
  final ACC_TYPE type;
  final String zoneName;
  Map<String, dynamic> cmd;

  AccData({
    required this.type,
    required this.zoneName,
    required this.cmd
  });


  factory AccData.fromJson(ACC_TYPE accType, dynamic json){
    return AccData(
      type: accType,
      zoneName: json["zone"] ?? '',
      cmd: getCMDData(accType, json),
    );
  }

  static Map<String, dynamic> getCMDData(ACC_TYPE accType, dynamic json){
    if(accType == ACC_TYPE.ADMIN){
      return json["menu"] ?? {};
    }
    else if(accType == ACC_TYPE.USER){
      return json["cmd"] ?? {};
    }
    else {
      return {};
    }
  }

  Map<String, dynamic> toJson(){
    Map<String, dynamic> map = {};
    map['type'] = this.type.getName();
    map['zone'] = this.zoneName;
    map['cmd'] = this.cmd;
    return map;
  }

  @override
  String toString() {
    return jsonEncode(toJson());
  }

}

enum ACC_TYPE {
  NONE,
  ADMIN,
  USER
}

extension ACC_TYPE_EXT on ACC_TYPE{

  static ACC_TYPE parse(int typeID){
    return ACC_TYPE.values.firstWhere((element) {
      return element.getId() == typeID;
    }, orElse: () => ACC_TYPE.NONE);
  }

  static ACC_TYPE parseByName(String typeName){
    return ACC_TYPE.values.firstWhere((element) {
      return element.getName() == typeName;
    }, orElse: () => ACC_TYPE.NONE);
  }

  _accTypeID(ACC_TYPE type){
    switch(type){
      case ACC_TYPE.NONE: {
        return 0;
      }
      case ACC_TYPE.ADMIN: {
        return 1;
      }
      case ACC_TYPE.USER: {
        return 2;
      }
      default:{
        return 0;
      }
    }
  }

  int getId(){
    return _accTypeID(this);
  }

  _accTypeName(ACC_TYPE type){
    switch(type){
      case ACC_TYPE.NONE: {
        return "NONE";
      }
      case ACC_TYPE.ADMIN: {
        return "ADMIN";
      }
      case ACC_TYPE.USER: {
        return "USER";
      }
      default:{
        return "NONE";
      }
    }
  }

  getName(){
    return _accTypeName(this);
  }

}

