  static parse(int pID){
    return PRIVILEGE.values.firstWhere((element){
      return element.getId() == pID;
    }, orElse: () => PRIVILEGE.NONE);
  }

  static parseByName(String pName){
    return PRIVILEGE.values.firstWhere((element){
      return element.getName() == pName;
    }, orElse: () => PRIVILEGE.NONE);
  }

  _priviType(PRIVILEGE type){
    switch(type){
      case PRIVILEGE.NONE:{
        return 0;
      }
      case PRIVILEGE.ADMIN:{
        return 1;
      }
      case PRIVILEGE.MODERATOR:{
        return 2;
      }
      case PRIVILEGE.SUPER_USER:{
        return 3;
      }
      case PRIVILEGE.HTTP_USER:{
        return 4;
      }
      case PRIVILEGE.LINKED_USER:{
        return 5;
      }
      case PRIVILEGE.USER:{
        return 9;
      }
      default:{
        return 0;
      }
    }
  }

  int getId(){
    return _priviType(this);
  }

  _priviName(PRIVILEGE type){
    switch(type){
      case PRIVILEGE.NONE:{
        return "NONE";
      }
      case PRIVILEGE.ADMIN:{
        return "ADMIN";
      }
      case PRIVILEGE.MODERATOR:{
        return "MODERATOR";
      }
      case PRIVILEGE.SUPER_USER:{
        return "SUPER_USER";
      }
      case PRIVILEGE.HTTP_USER:{
        return "HTTP_USER";
      }
      case PRIVILEGE.LINKED_USER:{
        return "LINKED_USER";
      }
      case PRIVILEGE.USER:{
        return "USER";
      }
      default:{
        return "NONE";
      }
    }
  }

  getName(){
    return _priviName(this);
  }
}
